import java.util.Objects;

/**
 * Created by liuxz on 17-3-10.
 */
public class Leet115DistinctSubsequences {

    public int numDistinct(String t, String s) {
        if (s.length()> t.length()){
            return 0;
        }else if (s.length() == t.length()){
            return Objects.equals(s,t)?1:0;
        }
        s = '`'+s;t = '`'+t;
        int[][] matrix=new int[s.length()][t.length()];
        for (int i = 0 ; i < s.length(); i ++){
            for (int j = i ; j < t.length(); j ++){
                if ((i ==0) &&(j == 0)) {matrix[i][j]=1;}
                else if (i == 0){matrix[i][j] = matrix[i][j-1];}
                else if (j < i){matrix[i][j]=0;}
                else if (i == j){matrix[i][j] = ((s.charAt(i)==t.charAt(j))?matrix[i-1][j-1]:0);}
                else{
                    matrix[i][j] = matrix[i][j-1] + ((s.charAt(i)==t.charAt(j))?matrix[i-1][j-1]:0);
                }
            }
        }
//        pr(matrix);
        return matrix[s.length()-1][t.length()-1];
    }



    public int numDistinct2(String t, String s) {
        if (s.length()> t.length()){
            return 0;
        }else if (s.length() == t.length()){
            return Objects.equals(s,t)?1:0;
        }
        s = '`'+s;t = '`'+t;
        rep xx = new rep(s,t);
        return xx.getValues(s.length()-1, t.length()-1);
    }

    private class rep {
        String s;String t;
        int[][] matrix;
        boolean[][] touch;
        rep(String s, String t){
            this.s = s;
            this.t = t;
            matrix = new int[s.length()][t.length()];
            touch = new boolean[s.length()][t.length()];
        }

        private void setValues(int i, int j, int values){
            touch[i][j]=true;
            matrix[i][j]=values;
        }

        private int getValues(int i, int j){
            if (!touch[i][j]){
                int sum = 0 ;
                if ((i ==0) &&(j == 0)) {setValues(0,0,1);}
                else if (i == 0){setValues(i,j,getValues(i,j-1));}
                else if (j < i){setValues(i,j,0);}
                else if (i == j){setValues(i,j,(s.charAt(i)==t.charAt(j))?getValues(i-1,j-1):0);}
                else{
                    setValues(i,j,getValues(i,j-1) + ((s.charAt(i)==t.charAt(j))?getValues(i-1,j-1):0));
                }
            }
            touch[i][j]=true;
            return matrix[i][j];
        }
    }

    public static void main(String[] args){
        Leet115DistinctSubsequences xx = new Leet115DistinctSubsequences();
        System.out.println(xx.numDistinct("rabbbit","rabbit"));
//        System.out.println(xx.numDistinct(""
//                ,"rwmimatmhydhbujebqehjprarwfkoebcxxqfktayaaeheys"));
    }

    private void pr(int[][] matrix){
        for (int i = 0 ; i < matrix.length; i ++){
            for( int j = 0; j < matrix[i].length ; j++){
                System.out.print(matrix[i][j] +"\t");
            }
            System.out.println("\n");
        }
    }
}
